class Solution:
    def longestDupSubstring(self, S):
        prime = 2**63 - 1
        
        def valid(L):
            rolling_hash = 0
            power_last = pow(26, L, prime)
            
            for i in range(L):
                rolling_hash = (rolling_hash * 26 + ord(S[i])) % prime
                
            seen = {rolling_hash}
            for i in range(L, len(S)):
                rolling_hash = (rolling_hash * 26 + ord(S[i]) - power_last * ord(S[i - L])) % prime
                if rolling_hash in seen:
                    return i - L + 1
                else:
                    seen.add(rolling_hash)
                    
            return -1
        
        
        low = 0
        high = len(S) - 1
        res = 0
        
        while(low < high):
            mid = (low + high + 1) // 2
            is_valid = valid(mid)
            if (is_valid != -1):
                low = mid
                res = is_valid
            else:
                high = mid - 1
                
        return S[res: res + low]

